jQuery(document).ready(function($) {
	var TopFixMenu = $("#fixNav");
	var FixFlyOpt = $(".flyTool");
	$(window).scroll(function(){
		if($(this).scrollTop()>150){
			TopFixMenu.show();
			FixFlyOpt.find('ul').addClass('fixMenu');
		}else{
			TopFixMenu.hide();
			FixFlyOpt.find('ul').removeClass('fixMenu');
		}}
	);
	

	var cou = $(".coupon.open");
	cou.click(function(event) {
		/* Act on the event */
		url = $(this).find('a').attr('href');
		coupon = $(this).find('a').attr('id');
		window.open(url);
		prompt("Copy coupon giảm giá bên dưới khi thanh toán:",coupon);
		event.preventDefault();
	});
	
	$('.flyTool ul li').tooltipsy({
		offset: [5, 0],
		show: function (e, $el) {
	        $el.css({
	            'left': parseInt($el[0].style.left.replace(/[a-z]/g, '')) - 20 + 'px',
	            'opacity': '0.0',
	            'display': 'block'
	        }).animate({
	            'left': parseInt($el[0].style.left.replace(/[a-z]/g, '')) + 20 + 'px',
	            'opacity': '1.0'
	        }, 300);
	    },
	    hide: function (e, $el) {
	        $el.slideUp(100);
	    }
	});
	$("#moreVideo .jCarouselLite").jCarouselLite({
    auto: 5000,
    speed: 1000,
    visible: 5,
    mouseWheel: true
});
});